from redis import Redis
import json
import uuid
import time

class redis_test:
    def __init__(self, host, port, pipeline):
        self.host = host
        self.port = port
        self.pipeline = pipeline
        self.redis = Redis(host=host, port=port)

    def update_classifier(self, classifier_id):
        message = {'type':'train', 'classifier_id':classifier_id}
        self.redis.lpush(self.pipeline, json.dumps(message))

    def predict_documents(self, classifier_id, documents):
        response_key = uuid.uuid4().__str__()
        ids = []
        for i in range(len(documents)):
            ids.append(i)
        message = {'type':'predict', 'classifier_id':classifier_id, 'documents':documents, 'document_ids':ids, 'response_key':response_key}
        #predict message: {'type':'predict', 'classifier_id':'classifier_id', 'documents':[], 'document_ids':[], 'response_key':'response_key'}
        self.redis.lpush(self.pipeline, json.dumps(message))

        #query for results:
        while True:
            if self.redis.exists(response_key):
                print("Key exists")
                msg = self.redis.get(response_key)
                print(msg)
                break
            time.sleep(0.05)

